set logistics_no = #{logistics_no,jdbcType=VARCHAR},
  `state` = #{state,jdbcType=VARCHAR}
where 1=1 
and shop_order_id = #{shop_order_id,jdbcType=VARCHAR}
and goods_id = #{goods_id}

mapper.select:
