mapper.update:
update user_wallet

  
    future_currency = future_currency - #{future_currency},
  
  
    future_treasure = future_treasure - #{future_treasure},
  
  
    money = money - #{money},
  
  
    froze_wlbi_amnt = froze_wlbi_amnt - #{froze_wlbi_amnt},
  
  
    froze_wlbao_amnt = froze_wlbao_amnt - #{froze_wlbao_amnt},
  
  
    froze_rmb_amnt = froze_rmb_amnt - #{froze_rmb_amnt},
  
    last_caldate = now(),
  
    operator = #{operator,jdbcType=VARCHAR},
  

where 1=1
and user_id = #{user_id}

   and future_currency >= #{future_currency}
 
 
   and future_treasure >= #{future_treasure}
 
 
   and money >= #{money}
 
 
   and froze_wlbi_amnt >= #{froze_wlbi_amnt}
 
 
   and froze_wlbao_amnt >= #{froze_wlbao_amnt}
 
 
   and froze_rmb_amnt >= #{froze_rmb_amnt}

