UPDATE user_wallet t
SET
t.future_treasure = t.future_treasure - #{money},
t.froze_wlbao_amnt = ifnull(t.froze_wlbao_amnt,0) + #{money}
where t.user_id = #{user_id}
and t.future_treasure >= #{money}

